mapper.select:

SELECT *
FROM schedule
WHERE enabled = 1 AND email = #{email}
ORDER BY id DESC
LIMIT #{page}, #{pageSize}


SELECT *
FROM schedule
WHERE email = #{email}
ORDER BY id DESC
LIMIT #{page}, #{pageSize}

mapper.update:

UPDATE schedule
SET content = #{content,jdbcType=VARCHAR},
    enabled = #{enabled,jdbcType=TINYINT}
WHERE id = #{id}

mapper.select:

SELECT *
FROM diary
WHERE email = #{email}
ORDER BY id DESC
LIMIT #{page}, #{pageSize}

mapper.update:

UPDATE diary
SET topic   = #{topic,jdbcType=VARCHAR},
    content = #{content,jdbcType=VARCHAR},
    enabled = #{enabled,jdbcType=TINYINT}
WHERE id = #{id}

mapper.select:

SELECT *
FROM knowledge
WHERE email = #{email}
ORDER BY id DESC
LIMIT #{page}, #{pageSize}

mapper.update:

UPDATE knowledge
SET question    = #{question,jdbcType=VARCHAR},
    answer      = #{answer,jdbcType=VARCHAR},
    proficiency = #{proficiency,jdbcType=INTEGER},
    enabled     = #{enabled,jdbcType=TINYINT}
WHERE id = #{id}
